/* this function places the image into the pix_buf for sending it to the VGA via SPI and DMA */
void map_image_to_pix_buf(void)
{
    // image data has 480 lines, each 20 bytes, each 32 bits => 800 pixels
    
    // the image contains 480 lines
    for (int image_line = 0; image_line < 480; image_line++)
    {   // each line consists of 20 bytes (* 32 bits = 640 pixels)
        for (int image_byte = 0; image_byte < 20; image_byte++)
        {   // get the current byte of the image to be displayed
            uint32_t im_byte = my_image[image_line][image_byte];
            // examine all 32 bits, start at the highest bit
            for (int image_bit = 31; image_bit >= 0; image_bit--)
            {
                // the pixel buffer contains 4 pixels per byte, see the function prepare_demo_image()
                // thus the 32 bits of image needs to be distributed over 8 bytes of the pixel buffer
                int byte_in_pix_buf = 7 - (image_bit >> 2);
                // calculate the address in the pixel buffer:
                // + the image_line multiplied by the number of bytes per line in the pixel buffer
                // + each image byte is distributed over 8 pixel buffer bytes
                // + the byte_in_pix_buf, calculated above
                // NOTE: the '+ 31' below is to create the back porch of the vertical sync; the first 31 lines are empty
                int addr = (image_line + 31) * TX_FRAME_BYTES + image_byte * 8 + byte_in_pix_buf;
                // now calculate which bit in the pixel buffer we need to set or clear
                // TODO: for now only bit 1 (on the MaixGo the pin labelled LCD D1) is used, which is the '1' in the line below
                int bit_in_pix_buf = 1 + (image_bit & 0x03) * 8;

                // calculate the mask to set or clear the bit in the pixel buffer
                uint32_t mask = 1 << image_bit;
                // if the bit in the image is not set, the corresponding bit in the pixel buffer must be cleared, else it needs to be set
                if ((im_byte & mask) == 0)
                {
                    // clear bit in pix_buf
                    pix_buf[addr] &= ~(1 << bit_in_pix_buf);
                }
                else
                {
                    // set bit in pix_buf
                    pix_buf[addr] |= 1 << bit_in_pix_buf;
                }
            }
        }
    }
}

/* This function makes an empty image in pix_buf with only the horizontal sync signal. 
   At the end of this function the image is placed into the pix_buf */
void prepare_demo_image()
{
    for (int j = 0; j < LINES; j++)
    {
        int addr;
        for (int i = 0; i < TX_FRAME_BYTES; i++)
        {
            // bit 0 is the horizontal sync signal (on the MaixGo pin 'LCD D0')
            // bit 1 is the data signal (on the MaixGo pin 'LCD D1')

            // calculate which place in the buffer the data needs to be stored in
            addr = j * TX_FRAME_BYTES + i;

            // start with an empty image, only the horizontal sync signal is 1
            // 4 bytes in a 32-bit uint32_t: 00000001 00000001 00000001 00000001
            pix_buf[addr] = 16843009;

            // Note: the vertical sync signal is taken care of by the interrupt
            // NOTE: except the front and back porches!!!
            // add 31 empty lines in front of the image, this is taken care of in the function 'map_image_to_pix_buf'

            // horizontal sync signal:
            // make 96 horizontal sync bits 0's: clear the bits near the end of the line
            int start = 164;
            if ((i >= start) && (i < start + 24))
            {
                pix_buf[addr] &= ~16843009;
            }
        }
    }
    // add the image on top (actually besides) of the horizontal sync data
    map_image_to_pix_buf();
}

/* This function outputs a frame to the parallel SPI0 output via spi and dma */
void irq_frame()
{
    // initialize the SPI device:
    // mode 0: both clock polarity (CPOL) and clock phase (CPHA) are 0,
    // OCTAL means that an 8-bit wide databus is used (normal SPI has only 1 data line), 
    // the data to be send is 32-bits wide, little-endian
    spi_init((spi_device_num_t)SPI_CHANNEL, SPI_WORK_MODE_0, SPI_FF_OCTAL, 32, 0);
    // channel, instruction length, address length, wait cycles, spi address trans mode
    spi_init_non_standard((spi_device_num_t)SPI_CHANNEL, 0, 32, 0, SPI_AITM_AS_FRAME_FORMAT);
    // actually send the data via DMA
    spi_send_data_normal_dma(DMAC_CHANNEL0, (spi_device_num_t)SPI_CHANNEL, (spi_chip_select_t)SPI_SLAVE_SELECT, pix_buf, (TX_FRAME_BYTES * LINES), SPI_TRANS_INT);
}

/* This function sets up the interrupt to send out the data. A frequency of 60 Hz is used for VGA 640x480 */
void set_up_frame_interrupt()
{
    // use timer 0
    timer_init(TIMER_DEVICE_0);
    // a frame takes 16666667 nanoseconds to complete
    timer_set_interval(TIMER_DEVICE_0, TIMER_CHANNEL_0, 16666667);
    // have the interrupt call 'irq_frame' to start a new frame, priority 1
    timer_set_irq(TIMER_DEVICE_0, TIMER_CHANNEL_0, irq_frame, 1);

    // enable the pixel timer
    timer_set_enable(TIMER_DEVICE_0, TIMER_CHANNEL_0, 1);
    // switch on the interrupts
    sysctl_enable_irq();
}

int main(void)
{
    // convert the image to be displayed to the data to be send out via SPI
    prepare_demo_image();

    // don't know why this needs to be set, but it may make the spi0 a parallel output
    sysctl_set_spi0_dvp_data(1);
   
    // PLL0 is the basis for SPI
    sysctl_pll_set_freq(SYSCTL_PLL0, 403000000);

    uarths_init();
    plic_init();

    spi_set_clk_rate((spi_device_num_t)0, 25187500);
    set_up_frame_interrupt();

    while (1)
        ;

    return 0;
}
